function (
    strings: TemplateStringsArray,
    ...values: unknown[]
): string | TemplateResult {
    if (customTemplateLiteralTag) {
        return customTemplateLiteralTag(strings, ...values);
    }
    return values.reduce(
        (acc: string, v, idx) =>
            (acc as string) + (v as string) + strings[idx + 1],
        strings[0]
    );
}

