new FocusGroupController<AccordionItem>(this, {
        direction: 'vertical',
        elements: () => this.items,
        isFocusableElement: (el: AccordionItem) => !el.disabled,
    })

new RovingTabindexController<ActionButton>(
        this,
        {
            focusInIndex: (elements: ActionButton[]) => {
                let firstEnabledIndex = -1;
                const firstSelectedIndex = elements.findIndex((el, index) => {
                    if (!elements[firstEnabledIndex] && !el.disabled) {
                        firstEnabledIndex = index;
                    }
                    return el.selected && !el.disabled;
                });
                return elements[firstSelectedIndex]
                    ? firstSelectedIndex
                    : firstEnabledIndex;
            },
            elements: () => this.buttons,
            isFocusableElement: (el: ActionButton) => !el.disabled,
        }
    )

new ColorController(this, {
        extractColorFromState: () => ({
            h: this.hue,
            s: this.x,
            v: 1 - this.y,
        }),
        applyColorToState: ({ s, v }) => {
            this.x = s;
            this.y = 1 - v;
        },
    })

new ColorController(this, {
        /* c8 ignore next 3 */
        applyColorToState: () => {
            this.sliderHandlePosition = 100 * (this.colorController.hue / 360);
        },
        extractColorFromState: (controller) => ({
            ...(controller.getColor('hsl') as HSL),
            h: this.value,
        }),
        maintains: 'saturation',
    })

new ColorController(this, {
        /* c8 ignore next 3 */
