new RovingTabindexController<Radio>(this, {
        focusInIndex: (elements: Radio[]) => {
            return elements.findIndex((el) => {
                return this.selected
                    ? !el.disabled && el.value === this.selected
                    : !el.disabled;
            });
        },
        elementEnterAction: (el: Radio) => {
            this.selected = el.value;
        },
        elements: () => this.buttons,
        isFocusableElement: (el: Radio) => !el.disabled,
    })

new RovingTabindexController<SideNavItem>(this, {
        focusInIndex: (elements: SideNavItem[]) => {
            return elements.findIndex((el) => {
                return this.value
                    ? !el.disabled &&
                          !this.isDisabledChild(el) &&
                          el.value === this.value
                    : !el.disabled && !this.isDisabledChild(el);
            });
        },
        direction: 'vertical',
        elements: () => [...this.querySelectorAll('sp-sidenav-item')],
        isFocusableElement: (el: SideNavItem) =>
            !el.disabled && !this.isDisabledChild(el),
    })

