new RovingTabindexController<Tab>(this, {
        focusInIndex: (elements) => {
            let focusInIndex = 0;
            const firstFocusableElement = elements.find((el, index) => {
                const focusInElement = this.selected
                    ? !el.disabled && el.value === this.selected
                    : !el.disabled;
                focusInIndex = index;
                return focusInElement;
            });
            return firstFocusableElement ? focusInIndex : -1;
        },
        direction: () => 'both',
        elementEnterAction: (el) => {
            if (!this.auto) return;

            this.shouldAnimate = true;
            this.selectTarget(el);
        },
        elements: () => this.tabs,
        isFocusableElement: (el) => !el.disabled,
        listenerScope: () => this.tabList,
    })

new ResizeController(this, {
            target: this,
            callback: (): void => {
                this._updateScrollState();
            },
        })

new RovingTabindexController<Tag>(this, {
        focusInIndex: (elements: Tag[]) => {
            return elements.findIndex((el) => {
                return !el.disabled && el.deletable;
            });
        },
        elements: () => this.tags,
        isFocusableElement: (el: Tag) => !el.disabled && el.deletable,
